mapper.select:
SELECT * FROM ( SELECT rownum rnum, f.* FROM ( SELECT c.ani_no , userid, ani_cate, ani_name, ani_age , ani_gender, ani_neutral, ani_content, ani_hit, ani_weight , anifile_no, anifile_origin, anifile_stored , userno FROM( SELECT e.* FROM ( SELECT a.ani_no, userid, ani_cate, ani_name, ani_age , ani_gender, ani_neutral, ani_content, ani_hit, ani_weight , anifile_no, anifile_origin, anifile_stored, ROW_NUMBER() OVER(PARTITION BY a.ani_no ORDER BY anifile_no ASC) AS rn FROM adopt_pro a LEFT OUTER JOIN adopt_file b ON a.ani_no = b.ani_no)e WHERE rn = 1)c LEFT OUTER JOIN adopt_wish d ON c.ani_no = d.ani_no
WHERE ani_cate=#{aniCate }
ORDER BY ani_no DESC)f )list WHERE rnum BETWEEN #{paging.startNo } AND #{paging.endNo }

mapper.update:
UPDATE adopt_pro
SET ani_hit = ani_hit + 1
WHERE ani_no = #{aniNo}

UPDATE adopt_pro
SET ani_cate = #{aniCate}
	, ani_name = #{aniName}
	, ani_age = #{aniAge}, ani_gender = #{aniGender}
	, ani_neutral = #{aniNeutral}, ani_content = #{aniContent}
	, ani_weight = #{aniWeight}
WHERE ani_no = #{aniNo}

UPDATE adopt_cmt
SET procmt_content = #{procmtContent}
WHERE procmt_no = #{procmtNo}
